    if err := json.Unmarshal(notification.Params, &amp;message); err != nil {
        log.Printf(&quot;Failed to parse message notification: %v&quot;, err)
        return
    }

    fmt.Printf(&quot;Server message: %s\n&quot;, message.Text)
}

