    err := tc.Initialize(ctx)
    require.NoError(t, err)

    // Test tool call
    result, err := tc.CallTool(ctx, &quot;echo&quot;, map[string]interface{}{
        &quot;input&quot;: &quot;hello world&quot;,
    })
    require.NoError(t, err)
    assert.Equal(t, &quot;hello world&quot;, result.Content[0].Text)
}

