        return &amp;mcp.CallToolResult{
            Content: []mcp.Content{
                mcp.TextContent{
                    Type: &quot;text&quot;,
                    Text: fmt.Sprintf(&quot;LLM Response (model: %s): %s&quot;, 
                        result.Model, getTextFromContent(result.Content)),
