    )

    // Add a simple tool
    s.AddTool(
        mcp.NewTool(&quot;echo&quot;,
            mcp.WithDescription(&quot;Echo back the input&quot;),
            mcp.WithString(&quot;message&quot;, mcp.Required()),
        ),
        handleEcho,
    )

    // Choose transport based on environment
    transport := os.Getenv(&quot;MCP_TRANSPORT&quot;)
    port := os.Getenv(&quot;PORT&quot;)
    if port == &quot;&quot; {
        port = &quot;8080&quot;
    }

    switch transport {
    case &quot;sse&quot;:
        fmt.Printf(&quot;Starting SSE server on port %s\n&quot;, port)
