package main

import (
    &quot;context&quot;
    &quot;fmt&quot;
    &quot;log&quot;

    &quot;github.com/mark3labs/mcp-go/mcp&quot;
    &quot;github.com/mark3labs/mcp-go/server&quot;
    &quot;github.com/mark3labs/mcp-go/client&quot;
)

func main() {
    // Create server
    s := server.NewMCPServer(&quot;Calculator Server&quot;, &quot;1.0.0&quot;,
        server.WithToolCapabilities(true),
    )

    // Add calculator tool
    s.AddTool(
        mcp.NewTool(&quot;calculate&quot;,
            mcp.WithDescription(&quot;Perform basic mathematical calculations&quot;),
            mcp.WithString(&quot;operation&quot;, 
                mcp.Required(),
                mcp.Enum(&quot;add&quot;, &quot;subtract&quot;, &quot;multiply&quot;, &quot;divide&quot;),
                mcp.Description(&quot;The operation to perform&quot;),
            ),
            mcp.WithNumber(&quot;x&quot;, mcp.Required(), mcp.Description(&quot;First number&quot;)),
            mcp.WithNumber(&quot;y&quot;, mcp.Required(), mcp.Description(&quot;Second number&quot;)),
        ),
        handleCalculate,
    )

    // Create in-process client
    mcpClient, err := client.NewInProcessClient(s)
    if err != nil {
        log.Fatal(err)
    }
    defer mcpClient.Close()

    ctx := context.Background()

    // Initialize
    _, err = mcpClient.Initialize(ctx, mcp.InitializeRequest{
        Params: mcp.InitializeRequestParams{
            ProtocolVersion: &quot;2024-11-05&quot;,
            Capabilities: mcp.ClientCapabilities{
                Tools: &amp;mcp.ToolsCapability{},
            },
            ClientInfo: mcp.Implementation{
                Name:    &quot;test-client&quot;,
                Version: &quot;1.0.0&quot;,
            },
        },
    })
    if err != nil {
        log.Fatal(err)
    }

    // Use the calculator
    result, err := mcpClient.CallTool(ctx, mcp.CallToolRequest{
        Params: mcp.CallToolParams{
            Name: &quot;calculate&quot;,
            Arguments: map[string]interface{}{
                &quot;operation&quot;: &quot;add&quot;,
                &quot;x&quot;:         10.0,
                &quot;y&quot;:         5.0,
            },
        },
    })
    if err != nil {
        log.Fatal(err)
    }

    // Extract text from the first content item
    if len(result.Content) &gt; 0 {
        if textContent, ok := mcp.AsTextContent(result.Content[0]); ok {
            fmt.Printf(&quot;Result: %s\n&quot;, textContent.Text)
        }
    }
}

func handleCalculate(ctx context.Context, req mcp.CallToolRequest) (*mcp.CallToolResult, error) {
    operation := req.GetString(&quot;operation&quot;, &quot;&quot;)
    x := req.GetFloat(&quot;x&quot;, 0)
    y := req.GetFloat(&quot;y&quot;, 0)

    var result float64
    switch operation {
    case &quot;add&quot;:
        result = x + y
    case &quot;subtract&quot;:
        result = x - y
    case &quot;multiply&quot;:
        result = x * y
    case &quot;divide&quot;:
        if y == 0 {
            return mcp.NewToolResultError(&quot;division by zero&quot;), nil
        }
        result = x / y
    default:
        return nil, fmt.Errorf(&quot;unknown operation: %s&quot;, operation)
    }

    return mcp.NewToolResultText(fmt.Sprintf(&quot;%.2f&quot;, result)), nil
}

