    // Create OpenAI handler
    openaiHandler := NewOpenAISamplingHandler(&quot;your-api-key&quot;)

    // Create client with OpenAI sampling
    mcpClient, err := client.NewInProcessClientWithSamplingHandler(mcpServer, openaiHandler)
    if err != nil {
        log.Fatal(err)
    }

